# -*- coding: utf-8 -*-import xadmin# Register your models here.__author__ = 'Stone'__date__ = '2018/9/22'from xadmin import viewsfrom xadmin.plugins.auth import UserAdminfrom xadmin.layout import Fieldset,Main,Side,Rowfrom django.utils.translation import ugettext as _from Hikarian import modelsclass BaseSetting(object):    enable_themes = True    use_bootswatch = Trueclass GlobalSettings(object):    site_title = 'Hikarian_Manger'    site_footer = 'Hikarian'    menu_stytle = 'accordion'class UserInfoAdmin(object):    list_display = ['user_id', 'user_name', 'id_num', 'email', 'phone', 'money', 'is_delete',]    search_fields = ['user_id', 'user_name', 'id_num', 'is_delete']    list_filter = ['user_id', 'user_name', 'id_num', 'is_delete']class stationInfoAdmin(object):    list_display = ['station_id','station_name','city','province']    search_fields = ['station_id','station_name',]    list_filter = ['station_id','station_name','city','province',]class trainInfoAdmin(object):    list_display = ['train_id','train_type','startstation','endstation','departuretime','arrivaltime','carriagenum','stationnum','mail']    search_fields = ['train_id','train_type','startstation','endstation',]    list_filter = ['train_id','train_type','startstation','endstation',]class carriageInfoAdmin(object):    list_display = ['carriage_id','type','seat_num','unit_price',]    search_fields = ['carriage_id','type','seat_num','unit_price']    list_filter = ['carriage_id','type','seat_num','unit_price']class ticketInfoAdmin(object):    list_display = ['ticket_id','train_id','startstation','endstation','carriage_id','site','pay','departuretime','arrivaltime','is_valid']    search_fields = ['ticket_id','train_id','startstation','endstation','carriage_id']    list_filter = ['ticket_id','train_id','startstation','endstation','carriage_id']class trainToStationAdmin(object):    list_display = ['train_id','startstation','departuretime','duration','distance']    search_fields = ['train_id','startstation','departuretime','duration',]    list_filter = ['train_id','startstation','departuretime','duration']class trainCarriageAdmin(object):    list_display = ['train_id','carriage_id','carriage_num']    search_fields = ['train_id','carriage_id','carriage_num']    list_filter = ['train_id','carriage_id','carriage_num']class preplotAdmin(object):    list_display = ['preplot_id','user_id','date','is_paid']    search_fields = ['preplot_id','user_id','date','is_paid']    list_filter = ['preplot_id','user_id','date','is_paid']class ticketPreplotAdmin(object):    list_display = ['preplot_id','ticket_id','passenger']    search_fields = ['preplot_id','ticket_id','passenger']    list_filter = ['preplot_id','ticket_id','passenger']class ticketRefundAdmin(object):    list_display = ['preplot_id','ticket_id','refund','is_success']    search_fields = ['preplot_id','ticket_id','refund','is_success']    list_filter = ['preplot_id','ticket_id','refund','is_success']xadmin.site.register(models.userInfo,UserInfoAdmin)xadmin.site.register(models.trainInfo,trainInfoAdmin)xadmin.site.register(models.stationInfo,stationInfoAdmin)xadmin.site.register(models.carriageInfo,carriageInfoAdmin)xadmin.site.register(models.ticketInfo,ticketInfoAdmin)xadmin.site.register(models.trainToStation,trainToStationAdmin)xadmin.site.register(models.trainCarriage,trainCarriageAdmin)xadmin.site.register(models.preplot,preplotAdmin)xadmin.site.register(models.ticketPreplot,ticketPreplotAdmin)xadmin.site.register(models.ticketRefund,ticketRefundAdmin)xadmin.site.register(views.BaseAdminView,BaseSetting)xadmin.site.register(views.CommAdminView,GlobalSettings)